import { useState } from "react";
import "./App.css";

function App() {
  const [todo, setTodo] = useState("");
  const [todos, setTodos] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (todo) {
      setTodos([...todos, todo]);
      setTodo("");
    }
  };

  const handleDelete = (e) => {
    e.target.closest("p");
    console.log(e.target.closest("p").textContext);
  };
  return (
    <div className="App">
      <div className="container">
        <h1>To do List</h1>
        <form className="form" onSubmit={handleSubmit}>
          <input
            type="text"
            value={todo}
            placeholder="Enter your task "
            onChange={(e) => {
              setTodo(e.target.value);
            }}
          />
          <button>Go</button>
        </form>
        <div className="alltask">
          {todos.map((item, idx) => (
            <div className="task" key={idx}>
              <p>{item}</p>
              <button onClick={(e) => e.target.closest(".task").remove()}>
                Delete
              </button>
              <button onClick={(e) => handleDelete(e)}>Edit</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
