import { useState } from "react";
import "./App.css";

function App() {
  const [todo, setTodo] = useState("");
  const [todos, setTodos] = useState([]);
  const [edit, setEdit] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (edit) {
      const editTodo = todos.find((i) => i.id === edit.id);
    }
    if (todo !== "") {
      setTodos([...todos, { id: `${todo}-${Date.now()}`, todo }]);
      setTodo("");
    }
    if (setEdit) {
    }
  };

  const handleEdit = (e) => {
    const taskEl = e.target.closest(".task");
    const pEl = taskEl.children[0].textContent;
    setTodo(pEl);
    setEdit(true);
  };
  return (
    <div className="App">
      <div className="container">
        <h1>To do List</h1>
        <form className="form" onSubmit={handleSubmit}>
          <input
            type="text"
            value={todo}
            placeholder="Enter your task "
            onChange={(e) => {
              setTodo(e.target.value);
            }}
          />
          <button>{setEdit ? "GO" : "EDIT"}</button>
        </form>
        <div className="alltask">
          {todos.map((item, idx) => (
            <div className="task" key={idx}>
              <p className="task-content">{item}</p>
              <button onClick={(e) => e.target.closest(".task").remove()}>
                Delete
              </button>
              <button onClick={(e) => handleEdit(e)}>Edit</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
